import { getGifs } from "../helpers/getGifs.js/getGifs";


const useFetchGifs = (category) => {

    const[state, useState]=useState({
        data: [],
        loading: true
    });

    useEffect(() => {
        getGifs(category)
        .then(imgs=>{

            setTimeout(() => {

                setState({
                    data:imgs,
                    loading:false
                })

            }, 3000);

        })

    },[category])

}

export default useFetchGifs
