import { getGifs } from "../helpers/getGifs.js/getGifs";
import React,{useEffect} from "react";


const useFetchGifs = (category) => {

    const[state, setState]=useState({
        data: [],
        loading: true
    });

    useEffect(() => {
        getGifs(category)
        .then(imgs=>{

            setTimeout(() => {

                setState({
                    data:imgs,
                    loading:false
                })

            }, 3000);

        })

    },[category])

}

export default useFetchGifs
